mapper.select:
select *
from v_xjx_ksdf t
where t.lx=#{lx} and t.rq=#{rq}

    and t.ksmc like concat(concat('%',#{ksmc}),'%')


    and t.ksdm=#{ksdm}

order by t.ksdm

select t.rq,a.dept_name as ksmc,b.name,t.mzlfz,t.mzljf,t.mzljj, t.fhxfz,t.fhxjf,t.fhxjj,t.hxjffz as hxfz,t.hxjfjf as hxjf,t.hxjfjj as hxjj, t.ssfz,t.ssjf,t.ssjj,t.kfxmfz,t.kfxmjf,t.kfxmjj,t.qtxmzx as qtxmjf from XJX_DFMX t left join dept_dict a on t.ksdm=a.dept_code left join staff_dict b on t.zgh=b.user_name where t.rq = #{rq}
and a.dept_name like concat(concat('%',#{ksmc}),'%')
order by t.ksdm,t.zgh
